SELECT JOB.job_desc, OL.ord_no, OH.inv_no, OH.bill_to_name, qty_to_ship, entered_dt, inv_dt, OL.item_no, OL.item_desc_1, OL.item_desc_2, OH.job_no, OL.unit_price, (OL.unit_price * OL.qty_to_ship) AS ExtPrice FROM oelinhst_Sql OL JOIN OEHDRHST_SQL OH ON OH.inv_no = OL.inv_no JOIN dbo.jobfile_sql JOB ON JOB.job_no = OH.job_no WHERE OH.inv_dt > '01/01/2011' AND OH.inv_dt < '12/31/2011'